html.script:
function UpdateDisplayClass(checkbox, classname)
	{	
		var divs = document.getElementsByTagName("DIV");
		for (var i=0; ;i++)
		{
			if (i== divs.length) // needed to avoid "less then token" bug !!!
				return;
			if (divs[i].className == classname)
			{	
   				if (checkbox.checked)
   		   			divs[i].style.display = "inline";
				else
	   				divs[i].style.display = "none";
	   		}
		}
		return;
	}
